targets.expr:
sum_over_time(
    sum(meter{location="$Location",position="generator",type="gauge"}>0)[1d:5s] offset -1d
)  / 720 / 1000

- sum(increase(meter{location="$Location",position="grid",type="counter",direction="out"}[1d] offset -1d)/1000)
- min(
 sum_over_time((
    meter{location="$Location",position="battery",type="gauge"} <0)[1d:5s] offset -1d
 )  / 720 / 1000 *-1
) >=0
